package spikes.bamboo.queues;import java.io.PrintStream;import java.util.concurrent.BlockingQueue;public class Consumer extends Thread{  private BlockingQueue<String> blockingQueue;  private String consumerName;  private PrintStream printStream;  public Consumer(String consumerName_, BlockingQueue<String> blockingQueue_,     PrintStream printStream_)  {    consumerName = consumerName_;    blockingQueue = blockingQueue_;    printStream = printStream_;  }  @Override  public void run()  {    while (true)    {      try      {        Object value = blockingQueue.take();        if (value == null) continue;		printStream.println(this.consumerName + ": " + value);      }      catch (InterruptedException ie)      {        ie.printStackTrace();      }    }  }}