package spikes.bamboo.queues;import java.io.PrintStream;import java.util.Date;import java.util.Random;import java.util.concurrent.BlockingQueue;public class Producer extends Thread{  private BlockingQueue<String> blockingQueue;  private PrintStream printStream;  public Producer(BlockingQueue<String> blockingQueue_, PrintStream printStream_)  {    this.printStream = printStream_;    this.blockingQueue = blockingQueue_;  }    @Override  public void run()  {    try    {      while(true)      {        String time = String.format("%tc",new Date());        printStream.println("Putting item on BlockingQueue at time: " + time);        blockingQueue.put("Enqueued at: " + time);        Random generator = new Random();        int randomSleep = generator.nextInt(5) + 1;        Thread.sleep(randomSleep * 100);      }    }    catch (InterruptedException e)    {      e.printStackTrace();    }  }}