package spikes.bamboo.queues;import java.util.concurrent.BlockingQueue;import java.util.concurrent.LinkedBlockingQueue;public class TestBlockingQueue{  public static void main(String args[])  {    BlockingQueue<String> blockingQueue = new LinkedBlockingQueue<String>();    Producer producer = new Producer(blockingQueue, System.out);    Consumer consumerA = new Consumer("ConsumerA", blockingQueue, System.out);    Consumer consumerB = new Consumer("ConsumerB", blockingQueue, System.out);    Consumer consumerC = new Consumer("ConsumerC", blockingQueue, System.out);    Consumer consumerD = new Consumer("ConsumerD", blockingQueue, System.out);	    producer.start();    consumerA.start();    consumerB.start();    consumerC.start();    consumerD.start();    }}	